import { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { loginUser } from "../../api/users/usersAPI";

export default function LoginPage() {
    const [username, setUsername] = usesState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const { access, refresh } = await loginUser({ username, password });
            localStorage.setItem('access', access);
            localStorage.setItem('refresh', refresh);
            navigate('/reservations'); // Redirect to home page after successful login
        }
        catch (err) {
            settError('Invalid username or password');
        }
    };

    return (
        <div className="auth-page">
            <h2>Login</h2>
            {error && <p className="error">{error}</p>}
            <form onSubmit={handleSubmit} className="auth-form">
                <label>
                    User
                    <input
                        type="text"
                        value={username}
                        onChange={e => setUsername(e.target.value)}
                        required
                    />
                </label>
                <label>
                    Password
                    <input
                        type="password"
                        value={password}
                        onChange={e => setPassword(e.target.value)}
                        required
                    />
                </label>
                <button type="submit">Submit</button>
            </form>
            <p>
                Do not have an account? <Link to="/signup">Sign In</Link>
            </p>
        </div>
    );
};
